/* BASE USER TABLES */
CREATE TABLE IF NOT EXISTS employee (rootid varchar(64) NOT NULL PRIMARY KEY, tenantid varchar(64) NOT NULL, active BOOL DEFAULT TRUE, fname varchar(50) NOT NULL, lname varchar(50) NOT NULL, mobile varchar(15) UNIQUE NOT NULL, emailid varchar(50) UNIQUE, password varchar(255), profilepic varchar(100), timecreated bigint, timeupdated bigint, modifiedby varchar(64),  createdby varchar(64), locale varchar(12) default 'en_US', CONSTRAINT FOREIGN KEY(tenantid) REFERENCES tenant(rootid));
CREATE TABLE IF NOT EXISTS employeeinfo (rootid varchar(64) NOT NULL PRIMARY KEY, tenantid varchar(64) NOT NULL, active BOOL DEFAULT TRUE, lastactive bigint, lastlogin bigint, timecreated bigint, timeupdated bigint, modifiedby varchar(64), createdby varchar(64), CONSTRAINT FOREIGN KEY(tenantid) REFERENCES tenant(rootid));

CREATE TABLE IF NOT EXISTS customer (rootid varchar(64) NOT NULL PRIMARY KEY, tenantid varchar(64) NOT NULL, active BOOL DEFAULT TRUE, fname varchar(50) NOT NULL, lname varchar(50), mobile varchar(15) UNIQUE, emailid varchar(50) UNIQUE, password varchar(255), origin varchar(10), profilepic varchar(100), timecreated bigint, timeupdated bigint, modifiedby varchar(64), createdby varchar(64), CONSTRAINT FOREIGN KEY(tenantid) REFERENCES tenant(rootid));